import React, { useState } from 'react'
import { connect } from 'react-redux'
import s from 'styled-components'
import Popup from 'reactjs-popup'

import { editPost } from '../actions'

const Styledbutton = s.button`
  background-color: #F2ECAD;
  width: 5%;
  height: 5%;
  font-size: 16px;
  font-family: monospace;
  border-width: 2px;
  border-radius: 5px;
  right: 75px;
`

const Styledbutton1 = s.button`
  width: 20%;
  height: 10%;
  font-size: 16px;
  font-family: monospace;
  border-width: 2px;
  border-radius: 5px;
  position:relative
`
const Styledmenu = s.div`
  width: 400px;
  height: 400px;
  border-width: 1.5px;
  border-style: solid;
  border-color: black;
  top: 75px;
  border-radius: 3px;
  align-items: center;
  position: relative
`

const Styleditem = s.input`
  width: 350px;
  height: 35px;
  border-radius: 3px;
  border-width: 1.5px;
  border-style: solid;
  border-color: black;
  align-items: center;
  left: 25;
  position: absolute
`

const Styleddesc = s.h2`
  font-family: monospace;
  position: absolute
`

const EditPost = ({ dispatchEditPost, post}) => {
  const [newTitle, setNewTitle] = useState('')
  const [newImage, setNewImage] = useState('')
  const [newDesc, setNewDesc] = useState('')
  console.log(post.id)
  const [popUp, setNewPop] = useState(false)

  return (
    <>
      <Styledbutton style={{ position: 'fixed' }} onClick={() => { 
        setNewPop(true); console.log(post) }}>Edit Post</Styledbutton>
      <Popup open={popUp} closeOnDocumentClick onClose={!popUp} modal nested>
        <Styledmenu>
          <ul>
            <Styleddesc style={{ top: '40px' }}>Title</Styleddesc>
            <Styleditem style={{ top: '85px' }} onChange={e => setNewTitle(e.target.value)} />
            <Styleddesc style={{ top: '120px' }}>Image</Styleddesc>
            <Styleditem style={{ top: '165px' }} onChange={f => setNewImage(f.target.value)} />
            <Styleddesc style={{ top: '200px' }}>Desc</Styleddesc>
            <Styleditem style={{ top: '245px' }} onChange={g => setNewDesc(g.target.value)} />
          </ul>
          <Styledbutton1
            style={{ 'background-color': '#B2F2AD', left: '15px', top: '300px' }}
            onClick={() => {
              dispatchEditPost(newTitle, newImage, newDesc, post.id); setNewPop(false);console.log(post.id)
            }}>
            Save
          </Styledbutton1>
          <Styledbutton1
            style={{ 'background-color': '#ADF2E4', left: '30px', top: '300px' }} 
            onClick={() => setNewPop(false)}>
            Cancel
          </Styledbutton1>
        </Styledmenu>
      </Popup>

    </>
  )
}

const mapDispatchToProps = dispatch => ({
  dispatchEditPost: (title, image, text, id) => dispatch(editPost(title, image, text, id)),
})

const mapStateToProps = state => ({
  post: state.post,
})

export default connect(mapStateToProps, mapDispatchToProps)(EditPost)
